mapper.select:
select idr.comCode ,insurIdx ,insurCode ,makerCode ,custTypeCode ,dcRate ,startYmd ,endYmd ,memo ,idr.regUserId ,idr.created ,idr.uptUserId ,idr.modified ,u1.userName regUserName , u2.userName uptUserName from panErp.dbo.e_insurDcRate idr WITH(NOLOCK) LEFT JOIN dbo.e_user u1 WITH(NOLOCK) ON u1.comCode = idr.comCode AND u1.userId = idr.regUserId LEFT JOIN dbo.e_user u2 WITH(NOLOCK) ON u2.comCode = idr.comCode AND u2.userId = idr.uptUserId where 1=1 AND idr.comCode=#{logComCode}
AND insurCode = #{insurDcRate.insurCode}
AND makerCode = #{insurDcRate.makerCode}
AND endYmd is null
ORDER BY insurCode, makerCode, custTypeCode , startYmd

