mapper.select:
select a.comCode ,a.wdNo ,a.wdDate ,a.custCode ,cust2.custName AS custName ,a.accCode ,code.codeName AS accName ,a.payType ,code2.codeName AS payTypeName ,a.payCode ,payment.name AS payCodeName ,payment.accoutNo AS payAccNo ,a.supPrice ,a.vat ,a.wdMoney ,a.fee ,a.dcMoney ,a.wdReqNo ,a.memo ,a.cashM ,a.cardM ,a.accM, u1.userName AS regUserId , a.created ,
u2.userName AS uptUserId ,a.modified ,dd.withdrawDead ,a.clReqNo ,a.countY , CASE WHEN a.cashRectYN = 'N' THEN '' ELSE a.cashRectYN END AS cashRectYN ,a.cashRectM ,a.cashRectNo from panErp.dbo.e_withdraw a WITH(NOLOCK)
LEFT OUTER JOIN panErp.dbo.e_code code WITH(NOLOCK) ON code.mCode = '3000' AND a.accCode = code.code AND a.comCode = code.comCode LEFT OUTER JOIN panErp.dbo.e_code code2 WITH(NOLOCK) ON code2.mCode = '3010' AND a.payType = code2.code AND a.comCode = code2.comCode LEFT OUTER JOIN panErp.dbo.e_cust cust2 WITH(NOLOCK) ON a.custCode = cust2.custCode AND a.comCode = cust2.comCode LEFT OUTER JOIN panErp.dbo.e_payment payment WITH(NOLOCK) ON a.comCode = payment.comCode AND a.payCode = payment.payCode
LEFT OUTER JOIN panErp.dbo.e_deadline dd WITH(NOLOCK) ON a.comCode = dd.comCode LEFT OUTER JOIN panErp.dbo.e_user u1 WITH(NOLOCK) ON a.comCode = u1.comCode AND a.regUserId = u1.userId LEFT OUTER JOIN panErp.dbo.e_user u2 WITH(NOLOCK) ON a.comCode = u2.comCode AND a.uptUserId = u2.userId
where 1=1 AND a.comCode=#{comCode}
AND a.custCode = #{withdraw.custCode}
AND a.payCode = #{withdraw.payCode1}
AND a.payCode = #{withdraw.payCode2}
AND a.payType = #{withdraw.payType}
AND a.wdReqNo = #{withdraw.wdReqNo}
AND a.wdNo = #{withdraw.wdNo}
AND a.cashRectYN = 'Y'
AND a.cashRectYN = 'N'
AND a.memo LIKE CONCAT('%', #{withdraw.memo}, '%')
AND a.wdDate >= #{sYmd} and wdDate <= #{eYmd}
ORDER BY a.wdNo
ORDER BY a.wdDate, a.wdNo

SELECT z.*, y.custName
FROM (
SELECT '입금' dwType, depositDate dwDate, depositNo dwNo, a.accCode
,a.custCode ,depositMoney dpMoney ,0 wdMoney ,a.memo
,a.carNo ,a.payCode ,accCode accName
,a.comCode
from dbo.e_deposit a WITH(NOLOCK) 

WHERE depositDate BETWEEN #{sYmd} AND #{eYmd} AND a.comCode=#{logComCode}

UNION ALL

SELECT '출금' dwType, wdDate dwDate, wdNo dwNo, a.accCode ,a.custCode , 0
dpMoney ,wdMoney ,a.memo
,'' carNo ,a.payCode ,accCode accName
,a.comCode
from dbo.e_withdraw a WITH(NOLOCK) 

WHERE wdDate BETWEEN #{sYmd} AND #{eYmd} AND a.comCode=#{logComCode}
) z

join dbo.e_cust y  WITH(NOLOCK) ON z.custCode = y.custCode AND z.comCode = y.comCode
where 1=1 AND y.comCode=#{logComCode}

	AND z.payCode = #{accountHistory.payCode}


order by z.dwNo

SELECT t.comCode ,t.taxBillNo ,t.appStatusCode ,t.docStatusCode ,t.documentId ,t.documentId2 ,t.taxBillDate ,t.bizType ,t.custCode ,t.custName ,t.ceoName ,t.bizNo ,t.address ,t.phone ,t.fax ,t.taxEmail ,t.itemName ,t.supPrice ,t.vat ,t.totalPrice ,t.clType ,t.memo ,t.taxTypeCode ,t.taxTypeName ,t.errCode
,ISNULL(u1.userName,'') regUserId ,t.created ,ISNULL(u2.userName,'') uptUserId ,t.modified ,t.expType ,cg.clType AS clType2
FROM panErp.dbo.e_taxBill t WITH(NOLOCK) LEFT OUTER JOIN panErp.dbo.e_user u1 WITH(NOLOCK) ON u1.comCode = t.comCode AND u1.userId = t.regUserId LEFT OUTER JOIN panErp.dbo.e_user u2 WITH(NOLOCK) ON u2.comCode = t.comCode AND u2.userId = t.uptUserId LEFT OUTER JOIN ( SELECT tc.comCode,tc.taxBillNo,MIN(tc.clGroupId) clGroupId FROM panErp.dbo.e_taxBillClGroup tc WHERE tc.comCode = #{comCode} GROUP BY tc.comCode ,tc.taxBillNo) tc ON tc.comCode = t.comCode AND tc.taxBillNo = t.taxBillNo LEFT OUTER JOIN panErp.dbo.e_clGroup cg ON cg.comCode = tc.comCode AND cg.clGroupId = tc.clGroupId WHERE 1=1 AND t.comCode=#{comCode}
AND t.taxBillNo = #{taxBill.taxBillNo}
AND t.taxBillDate >= #{sYmd} and t.taxBillDate <= #{eYmd}
AND t.custCode = #{taxBill.custCode}
AND (t.appStatusCode = '' OR t.appStatusCode IS NULL)
AND (t.appStatusCode = 'W')
AND (t.appStatusCode = 'T' AND t.errCode = 'SUC001' )
AND (t.appStatusCode = 'B' )
AND t.expType = #{taxBill.expType}
AND cg.clType = #{taxBill.clType2}

